class Fibonacci:
   def __init__(self, max):
       self.max = max

   def sequence(self):
       a, b = 0, 1
       while a < self.max:
           yield a
           a, b = b, a + b

 fib = Fibonacci(100)
 print(list(fib.sequence()))

